import React from 'react';
import {shoes} from './Product.js';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import {useDispatch, useSelector} from 'react-redux'
import { add } from './BasketSlice';


import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams
  } from "react-router-dom";

const useStyles = makeStyles({
img: {
    width: 450,
    height:450
},
p:{
    textAlign:"left"
},
});

export default function ProductDescription() {
    const {id} = useParams()
    const shoe = shoes[id]
    const classes = useStyles();

    const dispatch = useDispatch();
    const counter = useSelector( (state)=> {
        console.log("jootay",state);
        return state.basket.shoes
    });

    return (
        <div >
          <Grid container spacing={1}>
            <Grid item xs={12}>
              <h1>{shoe.name}</h1>
            </Grid>
            <Grid item xs={6}>
                <img src={shoe.img} className={classes.img} alt={id} />
            </Grid>
            <Grid item xs={6} className={classes.p}>
              <p><strong>Description: </strong>{shoe.description}</p>
              <Grid item xs={12}>
                <p><strong>Price: </strong>{shoe.price}$</p>
              </Grid>
              <button onClick={dispatch(add(shoe))}>Add to cart</button>
            </Grid>
          </Grid>
        </div>
      );
}
