import { createSlice } from "@reduxjs/toolkit";

export const basketSlice = createSlice({
    name: "Basket",
    initialState : {
        shoes:{}  
    },
    reducers: {
        add: (state,action)=>{
              console.log(action.payload)
              state.shoes=action.payload
        }

    }
});

export const { add } = basketSlice.actions;
export default basketSlice.reducer;
