import React from 'react';
import {shoes} from './Product.js';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams
  } from "react-router-dom";

const useStyles = makeStyles({
root: {
    flexGrowL:1,
},
link:{
    padding:10,
},
title: {
    fontSize: 16,
    fontFamily: Arial,
},
pos: {
    marginBottom: 12,
},
img: {
    borderRadius: 4,
    padding: 5,
    width: 250,
}
});



export default function ProductDescription(props) {
    const {id} = useParams()
    const shoe = shoes[id]
    const classes = useStyles();
    return (
        <div className={classes.root}>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <h1>{shoe.name}</h1>
            </Grid>
            <Grid item xs={6}>
            <img src={shoe.img} className={classes.img} alt={id} />
            </Grid>
            <Grid item xs={6}>
              <p>{shoe.price}</p>
              <p>{shoe.description}</p>
            </Grid>
          </Grid>
        </div>
      );
}
