import React from 'react'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
  import { makeStyles } from '@material-ui/core/styles';
  import Card from '@material-ui/core/Card';
  import CardActions from '@material-ui/core/CardActions';
  import CardContent from '@material-ui/core/CardContent';
  import Typography from '@material-ui/core/Typography';
  
  const useStyles = makeStyles({
    root: {
      minWidth: 275,
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    title: {
      fontSize: 16,
    },
    pos: {
      marginBottom: 12,
    },
  });
  
  export const shoes = {
    'air-jordan-3-valor-blue': {
      name: 'VALOUR BLUE',
      img:
        'https://secure-images.nike.com/is/image/DotCom/CT8532_104_A_PREM?$SNKRS_COVER_WD$&align=0,1',
      price: 200,
      description:
        'VALOUR BLUE will make you look becoming and give you a sense of pride and satisfaction, and will give you a sense of comfort and relief.',
    },
    'jordan-mars-270-london': {
      name: 'JORDAN MARS 270 LONDON',
      img:
        'https://secure-images.nike.com/is/image/DotCom/CV3042_001_A_PREM?$SNKRS_COVER_WD$&align=0,1',
      price: 250,
      description:
        'JORDAN MARS 270 LONDON will take you from season to season with effortless style and will make your feet feel in heaven.',
    },
    'air-jordan-1-zoom-racer-blue': {
      name: 'RACER BLUE',
      img:
        'https://secure-images.nike.com/is/image/DotCom/CK6637_104_A_PREM?$SNKRS_COVER_WD$&align=0,1',
      price: 300,
      description:
        'RACER BLUE will give you all the comfort with the cushioning support of a running shoe.',
    },
  }
  



  
export function Product() {
    
    const classes = useStyles();
    const bull = <span className={classes.bullet}>â€¢</span>;
  
    return (
        <div>
            <h1>Products</h1>
            <div>
            {Object.entries(shoes).map(
              ([id, { name, img, price, description }]) => (
                <Link to={`/product/${id}`} key={id}>
                    <Card className={classes.root} variant="outlined">
                        <CardContent>
                        <Typography className={classes.title} color="textSecondary" gutterBottom>
                        {name}
                        </Typography>
                        <img src={img} alt={id} />
                        <p className='price'>
                        Price: <bold>${price}</bold>
                        </p>
                        <p>{description.slice(0, 70)}...</p>
                        </CardContent>
                    </Card>
            </Link>)
            )}
            </div>
      </div>
    );
}
